import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { piNetworkService } from "./services/pi-network";
import { pricingService } from "./services/pricing";
import { sendPurchaseConfirmationEmail } from "./services/email";
import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import { insertUserSchema, insertPackageSchema, insertTransactionSchema } from "@shared/schema";
import { z } from "zod";

const JWT_SECRET = process.env.JWT_SECRET || process.env.SESSION_SECRET || 'fallback-secret';

export async function registerRoutes(app: Express): Promise<Server> {
  // Middleware for admin authentication
  const authenticateAdmin = async (req: any, res: any, next: any) => {
    const token = req.headers.authorization?.replace('Bearer ', '');
    if (!token) {
      return res.status(401).json({ message: 'No token provided' });
    }

    try {
      const decoded = jwt.verify(token, JWT_SECRET) as any;
      const admin = await storage.getAdminByUsername(decoded.username);
      if (!admin || !admin.isActive) {
        return res.status(401).json({ message: 'Invalid admin token' });
      }
      req.admin = admin;
      next();
    } catch (error) {
      return res.status(401).json({ message: 'Invalid token' });
    }
  };

  // Pi Network Authentication
  app.post('/api/auth/pi', async (req, res) => {
    try {
      const { accessToken } = req.body;
      if (!accessToken) {
        return res.status(400).json({ message: 'Access token required' });
      }

      const piUser = await piNetworkService.verifyAccessToken(accessToken);
      if (!piUser) {
        return res.status(401).json({ message: 'Invalid Pi Network token' });
      }

      // Check if user exists, if not create new user
      let user = await storage.getUserByPiUID(piUser.uid);
      if (!user) {
        // Create minimal user profile - they'll complete it later
        const newUser = {
          piUID: piUser.uid,
          username: piUser.username,
          email: '',
          phone: '',
          country: 'Bhutan',
          language: 'en',
          walletAddress: '', // Will be updated when they make their first transaction
        };
        user = await storage.createUser(newUser);
      }

      // Generate JWT token for session
      const token = jwt.sign({ userId: user.id, piUID: user.piUID }, JWT_SECRET, { expiresIn: '7d' });

      res.json({
        user: {
          id: user.id,
          username: user.username,
          email: user.email,
          phone: user.phone,
          country: user.country,
          language: user.language,
          gameAccounts: user.gameAccounts,
          walletAddress: user.walletAddress,
        },
        token,
      });
    } catch (error) {
      console.error('Pi authentication error:', error);
      res.status(500).json({ message: 'Authentication failed' });
    }
  });

  // Admin Authentication
  app.post('/api/admin/login', async (req, res) => {
    try {
      const { username, password } = req.body;
      if (!username || !password) {
        return res.status(400).json({ message: 'Username and password required' });
      }

      const admin = await storage.getAdminByUsername(username);
      if (!admin || !admin.isActive) {
        return res.status(401).json({ message: 'Invalid credentials' });
      }

      const isValidPassword = await bcrypt.compare(password, admin.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: 'Invalid credentials' });
      }

      await storage.updateAdminLastLogin(admin.id);

      const token = jwt.sign({ adminId: admin.id, username: admin.username }, JWT_SECRET, { expiresIn: '8h' });

      res.json({
        admin: {
          id: admin.id,
          username: admin.username,
          email: admin.email,
          role: admin.role,
        },
        token,
      });
    } catch (error) {
      console.error('Admin login error:', error);
      res.status(500).json({ message: 'Login failed' });
    }
  });

  // Get current Pi price
  app.get('/api/pi-price', async (req, res) => {
    try {
      const price = await pricingService.getCurrentPiPrice();
      const lastPrice = pricingService.getLastPrice();

      res.json({
        price,
        lastUpdated: lastPrice?.lastUpdated || new Date(),
      });
    } catch (error) {
      console.error('Pi price fetch error:', error);
      res.status(500).json({ message: 'Failed to fetch Pi price' });
    }
  });

  // Get packages with Pi pricing
  app.get('/api/packages', async (req, res) => {
    try {
      const packages = await storage.getActivePackages();
      const currentPiPrice = await pricingService.getCurrentPiPrice();

      const packagesWithPiPricing = packages.map(pkg => ({
        ...pkg,
        piPrice: pricingService.calculatePiAmount(parseFloat(pkg.usdtValue)),
        currentPiPrice,
      }));

      res.json(packagesWithPiPricing);
    } catch (error) {
      console.error('Packages fetch error:', error);
      res.status(500).json({ message: 'Failed to fetch packages' });
    }
  });

  // User profile update
  app.put('/api/profile', async (req, res) => {
    try {
      const token = req.headers.authorization?.replace('Bearer ', '');
      if (!token) {
        return res.status(401).json({ message: 'No token provided' });
      }

      const decoded = jwt.verify(token, JWT_SECRET) as any;
      const userId = decoded.userId;

      const updateData = req.body;

      // Validate required fields
      if (updateData.email && !updateData.email.endsWith('@gmail.com')) {
        return res.status(400).json({ message: 'Email must be a Gmail address' });
      }

      const updatedUser = await storage.updateUser(userId, updateData);
      if (!updatedUser) {
        return res.status(404).json({ message: 'User not found' });
      }

      res.json(updatedUser);
    } catch (error) {
      console.error('Profile update error:', error);
      res.status(500).json({ message: 'Profile update failed' });
    }
  });

  // Payment approval endpoint
  app.post('/api/payment/approve', async (req, res) => {
    try {
      const { paymentId } = req.body;
      if (!paymentId) {
        return res.status(400).json({ message: 'Payment ID required' });
      }

      // Get payment details from Pi Network
      const payment = await piNetworkService.getPayment(paymentId);
      if (!payment) {
        return res.status(404).json({ message: 'Payment not found' });
      }

      // Validate payment metadata
      if (!payment.metadata?.type || payment.metadata.type !== 'backend') {
        return res.status(400).json({ message: 'Invalid payment metadata' });
      }

      // Check if transaction already exists
      let transaction = await storage.getTransactionByPaymentId(paymentId);
      if (!transaction) {
        // Create new transaction record
        const currentPiPrice = await pricingService.getCurrentPiPrice();
        const usdAmount = pricingService.calculateUsdAmount(payment.amount);

        const transactionData = {
          userId: payment.metadata.userId,
          packageId: payment.metadata.packageId,
          paymentId: payment.identifier,
          piAmount: payment.amount.toString(),
          usdAmount: usdAmount.toString(),
          piPriceAtTime: currentPiPrice.toString(),
          status: 'pending',
          gameAccount: payment.metadata.gameAccount,
          metadata: payment.metadata,
        };

        transaction = await storage.createTransaction(transactionData);
      }

      // Approve payment with Pi Network
      const approved = await piNetworkService.approvePayment(paymentId);
      if (!approved) {
        await storage.updateTransaction(transaction.id, { status: 'failed' });
        return res.status(500).json({ message: 'Payment approval failed' });
      }

      // Update transaction status
      await storage.updateTransaction(transaction.id, { status: 'approved' });

      res.json({ success: true, transactionId: transaction.id });
    } catch (error) {
      console.error('Payment approval error:', error);
      res.status(500).json({ message: 'Payment approval failed' });
    }
  });

  // Payment completion endpoint
  app.post('/api/payment/complete', async (req, res) => {
    try {
      const { paymentId, txid } = req.body;
      if (!paymentId || !txid) {
        return res.status(400).json({ message: 'Payment ID and txid required' });
      }

      const transaction = await storage.getTransactionByPaymentId(paymentId);
      if (!transaction) {
        return res.status(404).json({ message: 'Transaction not found' });
      }

      // Complete payment with Pi Network
      const completed = await piNetworkService.completePayment(paymentId, txid);
      if (!completed) {
        await storage.updateTransaction(transaction.id, { status: 'failed' });
        return res.status(500).json({ message: 'Payment completion failed' });
      }

      // Update transaction with txid and completed status
      await storage.updateTransaction(transaction.id, { 
        status: 'completed', 
        txid: txid,
      });

      // Send confirmation email
      try {
        const user = await storage.getUser(transaction.userId);
        const pkg = await storage.getPackage(transaction.packageId);

        if (user && pkg && user.email) {
          const gameAccountString = transaction.gameAccount.ign 
            ? `${transaction.gameAccount.ign} (${transaction.gameAccount.uid})`
            : `${transaction.gameAccount.userId}:${transaction.gameAccount.zoneId}`;

          const emailSent = await sendPurchaseConfirmationEmail({
            to: user.email,
            username: user.username,
            packageName: pkg.name,
            piAmount: transaction.piAmount,
            usdAmount: transaction.usdAmount,
            gameAccount: gameAccountString,
            transactionId: transaction.id,
            paymentId: paymentId,
            isTestnet: process.env.NODE_ENV !== 'production',
          });

          await storage.updateTransaction(transaction.id, { emailSent });
        }
      } catch (emailError) {
        console.error('Email sending failed:', emailError);
        // Don't fail the transaction if email fails
      }

      res.json({ success: true, transactionId: transaction.id, txid });
    } catch (error) {
      console.error('Payment completion error:', error);
      res.status(500).json({ message: 'Payment completion failed' });
    }
  });

  // Get user transactions
  app.get('/api/transactions', async (req, res) => {
    try {
      const token = req.headers.authorization?.replace('Bearer ', '');
      if (!token) {
        return res.status(401).json({ message: 'No token provided' });
      }

      const decoded = jwt.verify(token, JWT_SECRET) as any;
      const userId = decoded.userId;

      const transactions = await storage.getUserTransactions(userId);
      res.json(transactions);
    } catch (error) {
      console.error('Transactions fetch error:', error);
      res.status(500).json({ message: 'Failed to fetch transactions' });
    }
  });

  // Admin endpoints
  app.get('/api/admin/analytics', authenticateAdmin, async (req, res) => {
    try {
      const analytics = await storage.getAnalytics();
      res.json(analytics);
    } catch (error) {
      console.error('Analytics fetch error:', error);
      res.status(500).json({ message: 'Failed to fetch analytics' });
    }
  });

  app.get('/api/admin/transactions', authenticateAdmin, async (req, res) => {
    try {
      const transactions = await storage.getAllTransactions();
      res.json(transactions);
    } catch (error) {
      console.error('Admin transactions fetch error:', error);
      res.status(500).json({ message: 'Failed to fetch transactions' });
    }
  });

  app.get('/api/admin/packages', authenticateAdmin, async (req, res) => {
    try {
      const packages = await storage.getPackages();
      res.json(packages);
    } catch (error) {
      console.error('Admin packages fetch error:', error);
      res.status(500).json({ message: 'Failed to fetch packages' });
    }
  });

  app.post('/api/admin/packages', authenticateAdmin, async (req, res) => {
    try {
      const packageData = insertPackageSchema.parse(req.body);
      const newPackage = await storage.createPackage(packageData);
      res.json(newPackage);
    } catch (error) {
      console.error('Package creation error:', error);
      res.status(500).json({ message: 'Failed to create package' });
    }
  });

  app.put('/api/admin/packages/:id', authenticateAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      const updateData = req.body;
      const updatedPackage = await storage.updatePackage(id, updateData);

      if (!updatedPackage) {
        return res.status(404).json({ message: 'Package not found' });
      }

      res.json(updatedPackage);
    } catch (error) {
      console.error('Package update error:', error);
      res.status(500).json({ message: 'Failed to update package' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  packages, 
  transactions, 
  admins, 
  piPriceHistory,
  type User, 
  type InsertUser,
  type Package,
  type InsertPackage,
  type Transaction,
  type InsertTransaction,
  type Admin,
  type InsertAdmin,
  type PiPriceHistory
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, count, sum, sql } from "drizzle-orm";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByPiUID(piUID: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, user: Partial<User>): Promise<User | undefined>;

  // Packages
  getPackages(): Promise<Package[]>;
  getActivePackages(): Promise<Package[]>;
  getPackage(id: string): Promise<Package | undefined>;
  createPackage(pkg: InsertPackage): Promise<Package>;
  updatePackage(id: string, pkg: Partial<Package>): Promise<Package | undefined>;

  // Transactions
  getTransaction(id: string): Promise<Transaction | undefined>;
  getTransactionByPaymentId(paymentId: string): Promise<Transaction | undefined>;
  getUserTransactions(userId: string): Promise<Transaction[]>;
  createTransaction(transaction: InsertTransaction): Promise<Transaction>;
  updateTransaction(id: string, transaction: Partial<Transaction>): Promise<Transaction | undefined>;
  getAllTransactions(): Promise<(Transaction & { user: User; package: Package })[]>;

  // Admins
  getAdminByUsername(username: string): Promise<Admin | undefined>;
  createAdmin(admin: InsertAdmin): Promise<Admin>;
  updateAdminLastLogin(id: string): Promise<void>;

  // Pi Price History
  savePiPrice(price: number): Promise<PiPriceHistory>;
  getLatestPiPrice(): Promise<PiPriceHistory | undefined>;

  // Analytics
  getAnalytics(): Promise<{
    totalUsers: number;
    totalTransactions: number;
    totalRevenue: number;
    successRate: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByPiUID(piUID: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.piUID, piUID));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values([insertUser]).returning();
    return user;
  }

  async updateUser(id: string, userData: Partial<User>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user || undefined;
  }

  async getPackages(): Promise<Package[]> {
    return await db.select().from(packages).orderBy(packages.game, packages.usdtValue);
  }

  async getActivePackages(): Promise<Package[]> {
    return await db
      .select()
      .from(packages)
      .where(eq(packages.isActive, true))
      .orderBy(packages.game, packages.usdtValue);
  }

  async getPackage(id: string): Promise<Package | undefined> {
    const [pkg] = await db.select().from(packages).where(eq(packages.id, id));
    return pkg || undefined;
  }

  async createPackage(insertPackage: InsertPackage): Promise<Package> {
    const [pkg] = await db.insert(packages).values(insertPackage).returning();
    return pkg;
  }

  async updatePackage(id: string, packageData: Partial<Package>): Promise<Package | undefined> {
    const [pkg] = await db
      .update(packages)
      .set({ ...packageData, updatedAt: new Date() })
      .where(eq(packages.id, id))
      .returning();
    return pkg || undefined;
  }

  async getTransaction(id: string): Promise<Transaction | undefined> {
    const [transaction] = await db.select().from(transactions).where(eq(transactions.id, id));
    return transaction || undefined;
  }

  async getTransactionByPaymentId(paymentId: string): Promise<Transaction | undefined> {
    const [transaction] = await db
      .select()
      .from(transactions)
      .where(eq(transactions.paymentId, paymentId));
    return transaction || undefined;
  }

  async getUserTransactions(userId: string): Promise<Transaction[]> {
    return await db
      .select()
      .from(transactions)
      .where(eq(transactions.userId, userId))
      .orderBy(desc(transactions.createdAt));
  }

  async createTransaction(insertTransaction: InsertTransaction): Promise<Transaction> {
    const [transaction] = await db.insert(transactions).values([insertTransaction]).returning();
    return transaction;
  }

  async updateTransaction(id: string, transactionData: Partial<Transaction>): Promise<Transaction | undefined> {
    const [transaction] = await db
      .update(transactions)
      .set({ ...transactionData, updatedAt: new Date() })
      .where(eq(transactions.id, id))
      .returning();
    return transaction || undefined;
  }

  async getAllTransactions(): Promise<(Transaction & { user: User; package: Package })[]> {
    return await db
      .select({
        id: transactions.id,
        userId: transactions.userId,
        packageId: transactions.packageId,
        paymentId: transactions.paymentId,
        txid: transactions.txid,
        piAmount: transactions.piAmount,
        usdAmount: transactions.usdAmount,
        piPriceAtTime: transactions.piPriceAtTime,
        status: transactions.status,
        gameAccount: transactions.gameAccount,
        metadata: transactions.metadata,
        emailSent: transactions.emailSent,
        createdAt: transactions.createdAt,
        updatedAt: transactions.updatedAt,
        user: users,
        package: packages,
      })
      .from(transactions)
      .leftJoin(users, eq(transactions.userId, users.id))
      .leftJoin(packages, eq(transactions.packageId, packages.id))
      .orderBy(desc(transactions.createdAt));
  }

  async getAdminByUsername(username: string): Promise<Admin | undefined> {
    const [admin] = await db.select().from(admins).where(eq(admins.username, username));
    return admin || undefined;
  }

  async createAdmin(insertAdmin: InsertAdmin): Promise<Admin> {
    const [admin] = await db.insert(admins).values(insertAdmin).returning();
    return admin;
  }

  async updateAdminLastLogin(id: string): Promise<void> {
    await db
      .update(admins)
      .set({ lastLogin: new Date() })
      .where(eq(admins.id, id));
  }

  async savePiPrice(price: number): Promise<PiPriceHistory> {
    const [priceRecord] = await db
      .insert(piPriceHistory)
      .values({ price: price.toString() })
      .returning();
    return priceRecord;
  }

  async getLatestPiPrice(): Promise<PiPriceHistory | undefined> {
    const [priceRecord] = await db
      .select()
      .from(piPriceHistory)
      .orderBy(desc(piPriceHistory.timestamp))
      .limit(1);
    return priceRecord || undefined;
  }

  async getAnalytics(): Promise<{
    totalUsers: number;
    totalTransactions: number;
    totalRevenue: number;
    successRate: number;
  }> {
    const [userCount] = await db.select({ count: count() }).from(users);
    const [transactionCount] = await db.select({ count: count() }).from(transactions);
    const [revenue] = await db
      .select({ total: sum(transactions.piAmount) })
      .from(transactions)
      .where(eq(transactions.status, "completed"));

    const [completedCount] = await db
      .select({ count: count() })
      .from(transactions)
      .where(eq(transactions.status, "completed"));

    const successRate = transactionCount.count > 0 
      ? (completedCount.count / transactionCount.count) * 100 
      : 0;

    return {
      totalUsers: userCount.count,
      totalTransactions: transactionCount.count,
      totalRevenue: parseFloat(revenue.total || "0"),
      successRate: Math.round(successRate * 100) / 100,
    };
  }
}

export const storage = new DatabaseStorage();

