@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=JetBrains+Mono:wght@400;500&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(222, 84%, 5%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 8%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222, 84%, 8%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(217, 91%, 60%);
  --primary-foreground: hsl(222, 84%, 5%);
  --secondary: hsl(262, 83%, 70%);
  --secondary-foreground: hsl(222, 84%, 5%);
  --muted: hsl(217, 33%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(262, 83%, 70%);
  --accent-foreground: hsl(222, 84%, 5%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 33%, 17%);
  --input: hsl(217, 33%, 17%);
  --ring: hsl(217, 91%, 60%);
  --radius: 0.75rem;
  --font-sans: Inter, system-ui, sans-serif;
  --font-mono: JetBrains Mono, monospace;

  /* Gaming theme colors */
  --gaming-blue: hsl(217, 91%, 60%);
  --gaming-purple: hsl(262, 83%, 70%);
  --gaming-green: hsl(142, 76%, 36%);
  --gaming-gold: hsl(45, 93%, 58%);
  --gaming-red: hsl(0, 84%, 60%);
}

.dark {
  --background: hsl(222, 84%, 5%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 8%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222, 84%, 8%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(217, 91%, 60%);
  --primary-foreground: hsl(222, 84%, 5%);
  --secondary: hsl(262, 83%, 70%);
  --secondary-foreground: hsl(222, 84%, 5%);
  --muted: hsl(217, 33%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(262, 83%, 70%);
  --accent-foreground: hsl(222, 84%, 5%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 33%, 17%);
  --input: hsl(217, 33%, 17%);
  --ring: hsl(217, 91%, 60%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
  }

  .font-mono {
    font-family: var(--font-mono);
  }
}

@layer components {
  .particles {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 0;
  }

  .particle {
    position: absolute;
    width: 3px;
    height: 3px;
    background: rgba(59, 130, 246, 0.6);
    border-radius: 50%;
    animation: float 6s ease-in-out infinite;
  }

  .gradient-border {
    background: linear-gradient(45deg, var(--gaming-blue), var(--gaming-purple));
    padding: 2px;
    border-radius: 12px;
  }

  .gradient-border-content {
    background: var(--card);
    border-radius: 10px;
  }

  .game-card {
    transition: all 0.3s ease;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(139, 92, 246, 0.1));
    backdrop-filter: blur(10px);
  }

  .game-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px rgba(59, 130, 246, 0.3);
  }

  .testnet-badge {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .pi-price-ticker {
    background: linear-gradient(90deg, var(--gaming-green), hsl(158, 64%, 52%));
    animation: glow 3s ease-in-out infinite alternate;
  }

  .social-icon {
    transition: all 0.3s ease;
  }

  .social-icon:hover {
    transform: translateY(-2px);
  }
}

@layer utilities {
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  @keyframes glow {
    0% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.4); }
    100% { box-shadow: 0 0 30px rgba(59, 130, 246, 0.8); }
  }

  @keyframes slideIn {
    0% { transform: translateX(-100%); opacity: 0; }
    100% { transform: translateX(0); opacity: 1; }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-slideIn {
    animation: slideIn 0.5s ease-out;
  }
}

