import { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { usePiNetwork } from '@/hooks/use-pi-network';
import { GAME_LOGOS } from '@/lib/constants';
import type { Package } from '@/types/pi-network';
import bcrypt from 'bcryptjs';

interface PurchaseModalProps {
  isOpen: boolean;
  onClose: () => void;
  package: Package;
}

export default function PurchaseModal({ isOpen, onClose, package: pkg }: PurchaseModalProps) {
  const { user, createPayment } = usePiNetwork();
  const { toast } = useToast();
  const [step, setStep] = useState<'confirm' | 'auth'>('confirm');
  const [passphrase, setPassphrase] = useState('');
  const [showPassphrase, setShowPassphrase] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [editableGameAccount, setEditableGameAccount] = useState<any>({});

  const gameLogoUrl = pkg.game === 'PUBG' ? GAME_LOGOS.PUBG : GAME_LOGOS.MLBB;
  const gameName = pkg.game === 'PUBG' ? 'PUBG Mobile' : 'Mobile Legends';

  useEffect(() => {
    if (user && isOpen) {
      if (pkg.game === 'PUBG' && user.gameAccounts?.pubg) {
        setEditableGameAccount(user.gameAccounts.pubg);
      } else if (pkg.game === 'MLBB' && user.gameAccounts?.mlbb) {
        setEditableGameAccount(user.gameAccounts.mlbb);
      } else {
        setEditableGameAccount({});
      }
    }
  }, [user, pkg.game, isOpen]);

  const handleConfirmPurchase = () => {
    // Validate game account
    if (pkg.game === 'PUBG') {
      if (!editableGameAccount.ign || !editableGameAccount.uid) {
        toast({
          title: "Error",
          description: "Please enter your PUBG IGN and UID",
          variant: "destructive",
        });
        return;
      }
    } else if (pkg.game === 'MLBB') {
      if (!editableGameAccount.userId || !editableGameAccount.zoneId) {
        toast({
          title: "Error",
          description: "Please enter your Mobile Legends User ID and Zone ID",
          variant: "destructive",
        });
        return;
      }
    }

    setStep('auth');
  };

  const handleProcessPayment = async () => {
    if (!passphrase) {
      toast({
        title: "Error",
        description: "Please enter your passphrase",
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);

    try {
      // Create payment with Pi Network
      const paymentData = {
        amount: pkg.piPrice || 0,
        memo: `${pkg.name} - ${gameName}`,
        metadata: {
          type: 'backend' as const,
          userId: user!.id,
          packageId: pkg.id,
          gameAccount: editableGameAccount,
          passphrase: await bcrypt.hash(passphrase, 10), // Hash passphrase
        },
      };

      createPayment(paymentData, {
        onReadyForServerApproval: (paymentId: string) => {
          toast({
            title: "Payment Approved",
            description: `Payment ${paymentId} approved by server`,
          });
        },
        onReadyForServerCompletion: (paymentId: string, txid: string) => {
          toast({
            title: "Payment Completed",
            description: `âœ… Payment confirmed! Transaction ID: ${txid}`,
          });
          onClose();
          setStep('confirm');
          setPassphrase('');
        },
        onCancel: (paymentId: string) => {
          toast({
            title: "Payment Cancelled",
            description: "âŒ Payment canceled. No Pi deducted.",
            variant: "destructive",
          });
          setIsProcessing(false);
        },
        onError: (error: Error) => {
          toast({
            title: "Payment Failed",
            description: `âš ï¸ Payment failed: ${error.message}. Please retry.`,
            variant: "destructive",
          });
          setIsProcessing(false);
        },
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Payment processing failed",
        variant: "destructive",
      });
      setIsProcessing(false);
    }
  };

  const handleGameAccountChange = (field: string, value: string) => {
    setEditableGameAccount((prev: any) => ({
      ...prev,
      [field]: value
    }));
  };

  const formatGameAccount = () => {
    if (pkg.game === 'PUBG') {
      return `${editableGameAccount.ign || 'Not set'} (${editableGameAccount.uid || 'Not set'})`;
    } else {
      return `${editableGameAccount.userId || 'Not set'}:${editableGameAccount.zoneId || 'Not set'}`;
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md" data-testid="purchase-modal">
        {step === 'confirm' ? (
          <>
            <DialogHeader>
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center mx-auto mb-4">
                  <i className="fas fa-gamepad text-2xl text-primary-foreground"></i>
                </div>
                <DialogTitle className="text-2xl mb-2">Confirm Purchase</DialogTitle>
                <p className="text-muted-foreground">Review and verify your purchase details</p>
              </div>
            </DialogHeader>

            <div className="space-y-4 mb-6">
              <Card>
                <CardContent className="p-4">
                  <div className="flex items-center">
                    <img src={gameLogoUrl} alt={gameName} className="w-12 h-12 mr-3" />
                    <div>
                      <p className="font-semibold" data-testid="package-name">{pkg.name}</p>
                      <p className="text-sm text-muted-foreground">{gameName}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-4">
                  <Label className="text-sm font-medium">Game Account</Label>
                  {pkg.game === 'PUBG' ? (
                    <div className="space-y-2 mt-2">
                      <Input
                        placeholder="In-Game Name (IGN)"
                        value={editableGameAccount.ign || ''}
                        onChange={(e) => handleGameAccountChange('ign', e.target.value)}
                        data-testid="purchase-pubg-ign"
                      />
                      <Input
                        placeholder="Player UID"
                        value={editableGameAccount.uid || ''}
                        onChange={(e) => {
                          const value = e.target.value.replace(/\D/g, '');
                          handleGameAccountChange('uid', value);
                        }}
                        className="font-mono"
                        data-testid="purchase-pubg-uid"
                      />
                    </div>
                  ) : (
                    <div className="space-y-2 mt-2">
                      <Input
                        placeholder="User ID"
                        value={editableGameAccount.userId || ''}
                        onChange={(e) => {
                          const value = e.target.value.replace(/\D/g, '');
                          handleGameAccountChange('userId', value);
                        }}
                        className="font-mono"
                        data-testid="purchase-mlbb-user-id"
                      />
                      <Input
                        placeholder="Zone ID"
                        value={editableGameAccount.zoneId || ''}
                        onChange={(e) => {
                          const value = e.target.value.replace(/\D/g, '');
                          handleGameAccountChange('zoneId', value);
                        }}
                        className="font-mono"
                        data-testid="purchase-mlbb-zone-id"
                      />
                    </div>
                  )}
                  <p className="text-xs text-amber-400 mt-2">
                    <i className="fas fa-exclamation-triangle mr-1"></i>
                    Please verify your game IDs are correct. Incorrect IDs may result in failed delivery.
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-4">
                  <div className="flex justify-between items-center">
                    <span className="font-medium">Total Cost</span>
                    <div className="text-right">
                      <p className="font-mono text-green-400 text-xl font-bold" data-testid="total-cost">
                        {pkg.piPrice?.toFixed(1)} Ï€
                      </p>
                      <p className="text-sm text-muted-foreground">â‰ˆ ${pkg.usdtValue}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {import.meta.env.DEV && (
              <div className="bg-amber-500/20 border border-amber-500 rounded-lg p-4 mb-6">
                <p className="text-sm text-amber-300">
                  <i className="fas fa-info-circle mr-2"></i>
                  Testnet Mode: No real Pi will be deducted from your mainnet wallet.
                </p>
              </div>
            )}

            <div className="flex space-x-4">
              <Button 
                variant="outline" 
                onClick={onClose} 
                className="flex-1"
                data-testid="cancel-purchase"
              >
                Cancel
              </Button>
              <Button 
                onClick={handleConfirmPurchase} 
                className="flex-1"
                data-testid="confirm-purchase"
              >
                Continue
              </Button>
            </div>
          </>
        ) : (
          <>
            <DialogHeader>
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center mx-auto mb-4">
                  <i className="fas fa-lock text-2xl text-primary-foreground"></i>
                </div>
                <DialogTitle className="text-2xl mb-2">Secure Payment</DialogTitle>
                <p className="text-muted-foreground">Enter your passphrase to authorize the payment</p>
              </div>
            </DialogHeader>

            <div className="space-y-4 mb-6">
              <Card>
                <CardContent className="p-4">
                  <div className="text-center">
                    <p className="text-lg font-semibold">{pkg.name}</p>
                    <p className="text-2xl font-bold text-green-400 font-mono">
                      {pkg.piPrice?.toFixed(1)} Ï€
                    </p>
                    <p className="text-sm text-muted-foreground">
                      Account: {formatGameAccount()}
                    </p>
                  </div>
                </CardContent>
              </Card>

              <div className="space-y-2">
                <Label>Payment Passphrase</Label>
                <div className="relative">
                  <Input
                    type={showPassphrase ? "text" : "password"}
                    value={passphrase}
                    onChange={(e) => setPassphrase(e.target.value)}
                    placeholder="Enter your secure passphrase"
                    disabled={isProcessing}
                    data-testid="payment-passphrase"
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-1 top-1/2 transform -translate-y-1/2"
                    onClick={() => setShowPassphrase(!showPassphrase)}
                    data-testid="toggle-passphrase"
                  >
                    <i className={`fas ${showPassphrase ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                  </Button>
                </div>
                <p className="text-xs text-muted-foreground">
                  This passphrase secures your Pi Network transactions
                </p>
              </div>
            </div>

            <div className="bg-blue-500/20 border border-blue-500 rounded-lg p-4 mb-6">
              <p className="text-sm text-blue-300">
                <i className="fas fa-info-circle mr-2"></i>
                You are about to pay {pkg.piPrice?.toFixed(1)} Ï€ for {pkg.name}. This transaction will be processed through Pi Network.
              </p>
            </div>

            <div className="flex space-x-4">
              <Button 
                variant="outline" 
                onClick={() => setStep('confirm')} 
                className="flex-1"
                disabled={isProcessing}
                data-testid="back-to-confirm"
              >
                Back
              </Button>
              <Button 
                onClick={handleProcessPayment} 
                className="flex-1"
                disabled={isProcessing || !passphrase}
                data-testid="process-payment"
              >
                {isProcessing ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Processing...
                  </>
                ) : (
                  <>
                    <i className="fas fa-credit-card mr-2"></i>
                    Pay Now
                  </>
                )}
              </Button>
            </div>
          </>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-pi-network.tsx.content:
import { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { piSDK } from '@/lib/pi-sdk';
import { apiRequest } from '@/lib/queryClient';
import type { User, PaymentData, PaymentCallbacks } from '@/types/pi-network';

interface PiNetworkContextType {
  isAuthenticated: boolean;
  user: User | null;
  isLoading: boolean;
  authenticate: () => Promise<void>;
  logout: () => void;
  createPayment: (paymentData: PaymentData, callbacks: PaymentCallbacks) => void;
  token: string | null;
}

const PiNetworkContext = createContext<PiNetworkContextType | undefined>(undefined);

interface PiNetworkProviderProps {
  children: ReactNode;
}

export function PiNetworkProvider({ children }: PiNetworkProviderProps) {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    // Initialize Pi SDK
    const isProduction = import.meta.env.PROD;
    piSDK.init(!isProduction); // sandbox mode for development

    // Check for existing session
    const savedToken = localStorage.getItem('pi_token');
    const savedUser = localStorage.getItem('pi_user');

    if (savedToken && savedUser) {
      setToken(savedToken);
      setUser(JSON.parse(savedUser));
      setIsAuthenticated(true);
    }

    setIsLoading(false);
  }, []);

  const authenticate = async () => {
    setIsLoading(true);
    try {
      const authResult = await piSDK.authenticate(['payments', 'username']);
      if (!authResult) {
        throw new Error('Authentication failed');
      }

      // Send access token to backend for verification
      const response = await apiRequest('POST', '/api/auth/pi', {
        accessToken: authResult.accessToken,
      });

      const data = await response.json();

      setUser(data.user);
      setToken(data.token);
      setIsAuthenticated(true);

      // Save to localStorage
      localStorage.setItem('pi_token', data.token);
      localStorage.setItem('pi_user', JSON.stringify(data.user));

    } catch (error) {
      console.error('Authentication error:', error);
      setIsAuthenticated(false);
      setUser(null);
      setToken(null);
    }
    setIsLoading(false);
  };

  const logout = () => {
    setIsAuthenticated(false);
    setUser(null);
    setToken(null);
    localStorage.removeItem('pi_token');
    localStorage.removeItem('pi_user');
  };

  const createPayment = (paymentData: PaymentData, callbacks: PaymentCallbacks) => {
    if (!isAuthenticated || !user) {
      throw new Error('User not authenticated');
    }

    // Add user context to metadata
    const enhancedPaymentData = {
      ...paymentData,
      metadata: {
        ...paymentData.metadata,
        type: 'backend' as const,
        userId: user.id,
      },
    };

    // Enhanced callbacks with API calls
    const enhancedCallbacks = {
      onReadyForServerApproval: async (paymentId: string) => {
        try {
          await apiRequest('POST', '/api/payment/approve', { paymentId });
          callbacks.onReadyForServerApproval(paymentId);
        } catch (error) {
          console.error('Payment approval failed:', error);
          callbacks.onError(error as Error);
        }
      },
      onReadyForServerCompletion: async (paymentId: string, txid: string) => {
        try {
          await apiRequest('POST', '/api/payment/complete', { paymentId, txid });
          callbacks.onReadyForServerCompletion(paymentId, txid);
        } catch (error) {
          console.error('Payment completion failed:', error);
          callbacks.onError(error as Error);
        }
      },
      onCancel: callbacks.onCancel,
      onError: callbacks.onError,
    };

    piSDK.createPayment(enhancedPaymentData, enhancedCallbacks);
  };

  return (
    <PiNetworkContext.Provider value={{
      isAuthenticated,
      user,
      isLoading,
      authenticate,
      logout,
      createPayment,
      token,
    }}>
      {children}
    </PiNetworkContext.Provider>
  );
}

export function usePiNetwork() {
  const context = useContext(PiNetworkContext);
  if (context === undefined) {
    throw new Error('usePiNetwork must be used within a PiNetworkProvider');
  }
  return context;
}

