import { usePiNetwork } from '@/hooks/use-pi-network';
import { usePiPrice } from '@/hooks/use-pi-price';
import { useQuery } from '@tanstack/react-query';
import { useLocation } from 'wouter';
import { useState } from 'react';
import ParticleBackground from '@/components/particle-background';
import Navigation from '@/components/navigation';
import Footer from '@/components/footer';
import ProfileModal from '@/components/profile-modal';
import PurchaseModal from '@/components/purchase-modal';
import PackageCard from '@/components/package-card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import type { Package, Transaction } from '@/types/pi-network';

export default function Dashboard() {
  const { user, isAuthenticated, logout, token } = usePiNetwork();
  const { data: piPrice } = usePiPrice();
  const [, setLocation] = useLocation();
  const [selectedPackage, setSelectedPackage] = useState<Package | null>(null);
  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);
  const [isPurchaseModalOpen, setIsPurchaseModalOpen] = useState(false);

  // Redirect to landing if not authenticated
  if (!isAuthenticated || !user) {
    setLocation('/');
    return null;
  }

  const { data: packages, isLoading: packagesLoading } = useQuery<Package[]>({
    queryKey: ['/api/packages'],
  });

  const { data: transactions, isLoading: transactionsLoading } = useQuery<Transaction[]>({
    queryKey: ['/api/transactions'],
    enabled: !!token,
  });

  const pubgPackages = packages?.filter(pkg => pkg.game === 'PUBG') || [];
  const mlbbPackages = packages?.filter(pkg => pkg.game === 'MLBB') || [];
  const recentTransactions = transactions?.slice(0, 5) || [];

  const handlePurchaseClick = (pkg: Package) => {
    setSelectedPackage(pkg);
    setIsPurchaseModalOpen(true);
  };

  const handleLogout = () => {
    logout();
    setLocation('/');
  };

  const formatWalletAddress = (address: string) => {
    if (!address) return 'Not set';
    return `${address.slice(0, 4)}...${address.slice(-4)}`;
  };

  const totalSpent = transactions?.reduce((sum, tx) => 
    tx.status === 'completed' ? sum + parseFloat(tx.piAmount) : sum, 0
  ) || 0;

  const completedTransactions = transactions?.filter(tx => tx.status === 'completed').length || 0;

  return (
    <div className="min-h-screen bg-background text-foreground" data-testid="dashboard-page">
      <ParticleBackground />
      <Navigation isTestnet={import.meta.env.DEV} />

      {/* Dashboard Header */}
      <div className="bg-card border-b border-border" data-testid="dashboard-header">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex justify-between items-center mb-6">
            <div>
              <h1 className="text-3xl font-bold text-foreground" data-testid="user-greeting">
                Welcome back, {user.username}!
              </h1>
              <p className="text-muted-foreground font-mono" data-testid="user-wallet">
                Wallet: {formatWalletAddress(user.walletAddress)}
              </p>
            </div>

            {/* Live Pi Price Ticker */}
            {piPrice && (
              <div className="pi-price-ticker px-6 py-3 rounded-lg" data-testid="pi-price-dashboard">
                <div className="text-center">
                  <p className="text-sm text-white/80">Live Pi Price</p>
                  <p className="text-2xl font-bold text-white">${piPrice.price.toFixed(3)}</p>
                  <p className="text-xs text-white/60">Updated 60s ago</p>
                </div>
              </div>
            )}
          </div>

          {/* Quick Stats */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6" data-testid="user-stats">
            <Card>
              <CardContent className="p-4">
                <h3 className="text-sm text-muted-foreground">Total Spent</h3>
                <p className="text-2xl font-bold text-green-400 font-mono" data-testid="stat-total-spent">
                  {totalSpent.toFixed(1)} Ï€
                </p>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-4">
                <h3 className="text-sm text-muted-foreground">Transactions</h3>
                <p className="text-2xl font-bold" data-testid="stat-transaction-count">
                  {completedTransactions}
                </p>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-4">
                <h3 className="text-sm text-muted-foreground">Country</h3>
                <p className="text-2xl font-bold text-blue-400" data-testid="stat-country">
                  {user.country}
                </p>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-4">
                <h3 className="text-sm text-muted-foreground">Status</h3>
                <p className="text-2xl font-bold text-green-400" data-testid="stat-status">
                  Active
                </p>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      {/* Dashboard Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Package Shop */}
          <div className="lg:col-span-2" data-testid="package-shop">
            <h2 className="text-2xl font-bold mb-6">Package Shop</h2>

            <Tabs defaultValue="pubg" className="w-full">
              <TabsList className="grid w-full grid-cols-2" data-testid="game-tabs">
                <TabsTrigger value="pubg" data-testid="tab-pubg">PUBG Mobile</TabsTrigger>
                <TabsTrigger value="mlbb" data-testid="tab-mlbb">Mobile Legends</TabsTrigger>
              </TabsList>

              <TabsContent value="pubg" className="mt-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6" data-testid="pubg-packages">
                  {packagesLoading ? (
                    <div className="col-span-2 text-center text-muted-foreground" data-testid="loading-packages">
                      Loading packages...
                    </div>
                  ) : (
                    pubgPackages.map((pkg) => (
                      <PackageCard
                        key={pkg.id}
                        package={pkg}
                        onPurchase={() => handlePurchaseClick(pkg)}
                        data-testid={`package-card-${pkg.id}`}
                      />
                    ))
                  )}
                </div>
              </TabsContent>

              <TabsContent value="mlbb" className="mt-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6" data-testid="mlbb-packages">
                  {packagesLoading ? (
                    <div className="col-span-2 text-center text-muted-foreground" data-testid="loading-packages">
                      Loading packages...
                    </div>
                  ) : (
                    mlbbPackages.map((pkg) => (
                      <PackageCard
                        key={pkg.id}
                        package={pkg}
                        onPurchase={() => handlePurchaseClick(pkg)}
                        data-testid={`package-card-${pkg.id}`}
                      />
                    ))
                  )}
                </div>
              </TabsContent>
            </Tabs>
          </div>

          {/* Sidebar */}
          <div className="space-y-8" data-testid="dashboard-sidebar">
            {/* Quick Actions */}
            <Card data-testid="quick-actions">
              <CardHeader>
                <CardTitle>Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button 
                  onClick={() => setIsProfileModalOpen(true)}
                  className="w-full bg-secondary hover:bg-secondary/90 text-secondary-foreground"
                  data-testid="button-edit-profile"
                >
                  <i className="fas fa-user mr-2"></i>Edit Profile
                </Button>
                <Button 
                  variant="outline"
                  className="w-full"
                  data-testid="button-view-history"
                >
                  <i className="fas fa-history mr-2"></i>View History
                </Button>
                <Button 
                  onClick={handleLogout}
                  variant="destructive"
                  className="w-full"
                  data-testid="button-logout"
                >
                  <i className="fas fa-sign-out-alt mr-2"></i>Logout
                </Button>
              </CardContent>
            </Card>

            {/* Recent Transactions */}
            <Card data-testid="recent-transactions">
              <CardHeader>
                <CardTitle>Recent Transactions</CardTitle>
              </CardHeader>
              <CardContent>
                {transactionsLoading ? (
                  <div className="text-muted-foreground" data-testid="loading-transactions">Loading...</div>
                ) : recentTransactions.length === 0 ? (
                  <div className="text-muted-foreground" data-testid="no-transactions">No transactions yet</div>
                ) : (
                  <div className="space-y-4">
                    {recentTransactions.map((transaction, index) => (
                      <div 
                        key={transaction.id} 
                        className="border-b border-border pb-3 last:border-b-0"
                        data-testid={`transaction-${index}`}
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <p className="font-semibold text-sm">
                              {packages?.find(p => p.id === transaction.packageId)?.name || 'Unknown Package'}
                            </p>
                            <p className="text-xs text-muted-foreground">
                              {new Date(transaction.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                          <div className="text-right">
                            <p className="font-mono text-green-400 text-sm">
                              -{transaction.piAmount} Ï€
                            </p>
                            <p className="text-xs capitalize text-green-400">
                              {transaction.status === 'completed' ? 'âœ… Completed' : 
                               transaction.status === 'pending' ? 'ðŸ”„ Pending' : 
                               transaction.status === 'failed' ? 'âŒ Failed' : transaction.status}
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      {/* Modals */}
      <ProfileModal 
        isOpen={isProfileModalOpen} 
        onClose={() => setIsProfileModalOpen(false)} 
      />

      {selectedPackage && (
        <PurchaseModal 
          isOpen={isPurchaseModalOpen} 
          onClose={() => {
            setIsPurchaseModalOpen(false);
            setSelectedPackage(null);
          }}
          package={selectedPackage}
        />
      )}

      <Footer />
    </div>
  );
}

client/src/pages/landing.tsx.content:
import { usePiNetwork } from '@/hooks/use-pi-network';
import { usePiPrice } from '@/hooks/use-pi-price';
import { useQuery } from '@tanstack/react-query';
import { useLocation } from 'wouter';
import ParticleBackground from '@/components/particle-background';
import Navigation from '@/components/navigation';
import Footer from '@/components/footer';
import { Button } from '@/components/ui/button';
import { BRAND_LOGOS, GAME_LOGOS } from '@/lib/constants';
import type { Package } from '@/types/pi-network';

export default function Landing() {
  const { authenticate, isAuthenticated, isLoading: piLoading } = usePiNetwork();
  const { data: piPrice } = usePiPrice();
  const [, setLocation] = useLocation();

  const { data: packages, isLoading: packagesLoading } = useQuery<Package[]>({
    queryKey: ['/api/packages'],
  });

  // Redirect to dashboard if already authenticated
  if (isAuthenticated) {
    setLocation('/dashboard');
    return null;
  }

  const handlePiLogin = async () => {
    try {
      await authenticate();
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  const featuredPubgPackages = packages?.filter(pkg => pkg.game === 'PUBG').slice(0, 2) || [];
  const featuredMlbbPackages = packages?.filter(pkg => pkg.game === 'MLBB').slice(0, 2) || [];

  return (
    <div className="min-h-screen bg-background text-foreground" data-testid="landing-page">
      <ParticleBackground />
      <Navigation isTestnet={import.meta.env.DEV} />

      {/* Hero Section */}
      <section className="min-h-screen flex items-center justify-center px-4 sm:px-6 lg:px-8" data-testid="hero-section">
        <div className="max-w-7xl w-full">
          <div className="text-center mb-16">
            <h1 className="text-5xl md:text-7xl font-bold mb-6 bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent" data-testid="hero-title">
              Gaming Currency
            </h1>
            <h2 className="text-3xl md:text-5xl font-bold mb-8 text-foreground" data-testid="hero-subtitle">
              Powered by Pi Network
            </h2>
            <p className="text-xl text-muted-foreground mb-12 max-w-3xl mx-auto" data-testid="hero-description">
              Purchase PUBG UC and Mobile Legends Diamonds using Pi coins. Fast, secure, and integrated with Pi Network's revolutionary payment system.
            </p>

            {/* Pi Network Login Button */}
            <div className="mb-8">
              <Button
                onClick={handlePiLogin}
                disabled={piLoading}
                className="gradient-border inline-block p-0 h-auto bg-transparent hover:bg-transparent"
                data-testid="pi-login-button"
              >
                <div className="gradient-border-content px-12 py-4 flex items-center space-x-4">
                  <img 
                    src={BRAND_LOGOS.PI} 
                    alt="Pi Network Logo" 
                    className="w-8 h-8 rounded-full"
                    data-testid="pi-logo-button"
                  />
                  <span className="text-xl font-semibold">
                    {piLoading ? 'Connecting...' : 'Sign In with Pi Network'}
                  </span>
                </div>
              </Button>

              {/* Testnet Disclaimer */}
              <p className="text-sm text-amber-400 mt-4 max-w-2xl mx-auto" data-testid="testnet-disclaimer">
                <i className="fas fa-info-circle mr-2"></i>
                Note: Transactions are currently processed on the Pi Testnet. No real Pi coins will be deducted from your mainnet wallet during purchases.
              </p>
            </div>
          </div>

          {/* Why Choose Us Section */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-20" data-testid="features-section">
            <div className="game-card p-8 rounded-xl text-center" data-testid="feature-pricing">
              <div className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-chart-line text-2xl text-white"></i>
              </div>
              <h3 className="text-xl font-semibold mb-3">Live Pricing</h3>
              <p className="text-muted-foreground">Real-time Pi/USD conversion rates updated every 60 seconds for accurate pricing.</p>
            </div>

            <div className="game-card p-8 rounded-xl text-center" data-testid="feature-security">
              <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-shield-alt text-2xl text-white"></i>
              </div>
              <h3 className="text-xl font-semibold mb-3">Secure Payments</h3>
              <p className="text-muted-foreground">Protected by Pi Network's advanced cryptographic payment system.</p>
            </div>

            <div className="game-card p-8 rounded-xl text-center" data-testid="feature-support">
              <div className="w-16 h-16 bg-purple-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-headset text-2xl text-white"></i>
              </div>
              <h3 className="text-xl font-semibold mb-3">24/7 Support</h3>
              <p className="text-muted-foreground">Round-the-clock customer support for all your gaming needs.</p>
            </div>
          </div>

          {/* Featured Packages */}
          <div className="text-center mb-12" data-testid="featured-packages">
            <h3 className="text-3xl font-bold mb-8">Featured Packages</h3>

            {/* Live Pi Price Display */}
            {piPrice && (
              <div className="mb-8">
                <div className="pi-price-ticker px-6 py-3 rounded-lg inline-block" data-testid="pi-price-ticker">
                  <div className="text-center">
                    <p className="text-sm text-white/80">Live Pi Price</p>
                    <p className="text-2xl font-bold text-white">${piPrice.price.toFixed(3)}</p>
                    <p className="text-xs text-white/60">Updated 60s ago</p>
                  </div>
                </div>
              </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-4xl mx-auto">
              {/* PUBG Package */}
              <div className="game-card p-8 rounded-xl" data-testid="featured-pubg">
                <div className="flex items-center justify-center mb-6">
                  <img 
                    src={GAME_LOGOS.PUBG} 
                    alt="PUBG Mobile Logo" 
                    className="w-16 h-16 mr-4"
                    data-testid="pubg-logo"
                  />
                  <h4 className="text-2xl font-bold">PUBG Mobile</h4>
                </div>
                <div className="space-y-4">
                  {featuredPubgPackages.map((pkg, index) => (
                    <div key={pkg.id} className="flex justify-between items-center p-3 bg-muted rounded-lg" data-testid={`pubg-package-${index}`}>
                      <span>{pkg.name}</span>
                      <span className="font-mono text-green-400">
                        {pkg.piPrice ? `${pkg.piPrice.toFixed(1)} Ï€` : 'Loading...'}
                      </span>
                    </div>
                  ))}
                  {packagesLoading && (
                    <div className="text-muted-foreground" data-testid="loading-packages">Loading packages...</div>
                  )}
                </div>
              </div>

              {/* MLBB Package */}
              <div className="game-card p-8 rounded-xl" data-testid="featured-mlbb">
                <div className="flex items-center justify-center mb-6">
                  <img 
                    src={GAME_LOGOS.MLBB} 
                    alt="Mobile Legends Logo" 
                    className="w-16 h-16 mr-4"
                    data-testid="mlbb-logo"
                  />
                  <h4 className="text-2xl font-bold">Mobile Legends</h4>
                </div>
                <div className="space-y-4">
                  {featuredMlbbPackages.map((pkg, index) => (
                    <div key={pkg.id} className="flex justify-between items-center p-3 bg-muted rounded-lg" data-testid={`mlbb-package-${index}`}>
                      <span>{pkg.name}</span>
                      <span className="font-mono text-green-400">
                        {pkg.piPrice ? `${pkg.piPrice.toFixed(1)} Ï€` : 'Loading...'}
                      </span>
                    </div>
                  ))}
                  {packagesLoading && (
                    <div className="text-muted-foreground" data-testid="loading-packages">Loading packages...</div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

